import { useState, useRef } from "react";

function BmiStatus({ bmi }) {
  if (bmi === 0) return <span>N/A</span>;

  if (bmi < 18.5)
    return (
      <span>
        <img
          src="data:image/jpeg;base64,"
          alt="underweight"
          width={128}
        />{" "}
        Underweight
      </span>
    );

  if (bmi < 24.9)
    return (
      <span>
        <img
          src="data:image/jpeg;base64,"
          alt="normal"
          width={128}
        />{" "}
        Normal weight
      </span>
    );

  if (bmi < 29.9)
    return (
      <span>
        <img
          src="data:image/jpeg;base64,"
          alt="overweight"
          width={128}
        />{" "}
        Overweight
      </span>
    );

  return (
    <span>
      <img
        src="data:image/jpeg;base64,"
        alt="obesity"
        width={128}
      />{" "}
      Obesity
    </span>
  );
}

export default function Bmi() {
  const w_InputRef = useRef();
  const h_InputRef = useRef();
  const [bmi, setBmi] = useState(0);

  function calculateBmi() {
    const height = parseFloat(h_InputRef.current.value);
    const weight = parseFloat(w_InputRef.current.value);
    if (height > 0 && weight > 0) {
      const bmiValue = parseFloat(
        (weight / (height / 100) ** 2).toFixed(2)
      );
      setBmi(bmiValue);
    } else {
      setBmi(0);
    }
  }

  return (
    <div style={{ textAlign: "center" }}>
      <input ref={w_InputRef} type="number" placeholder="Weight (kg)" />
      <input ref={h_InputRef} type="number" placeholder="Height (cm)" />
      <button onClick={calculateBmi}>Calculate</button>

      <h2>Your BMI: {bmi}</h2>
      <p>
        Status: <BmiStatus bmi={bmi} />
      </p>
    </div>
  );
}
